void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    float wiggle = sin(iTime*2.0+uv.x)*0.10;

    float x = length(vec2(uv.x*1.73,uv.y) - vec2(0.85, 0.5+wiggle));

    // Output to screen
    if(uv.y>0.75+wiggle)
    {
      fragColor = vec4(0.8,0,0,0);
    }
    else
    {
      fragColor = vec4(0,0,0.5,1);
    }
    if(uv.y<0.25+wiggle)
    {
      fragColor = vec4(0.8,0,0,0);
    }
    if(x<0.2)
    fragColor = vec4(1,1,1,1);

}
