// Based on: https://thebookofshaders.com/12/ by @patriciogv

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 st = fragCoord.xy/iResolution.xy;
    st.x *= iResolution.x/iResolution.y;
    float velocity = min(1.,iTime) * iTime;

    vec3 color = vec3(.0);

    vec2 point[21];
	point[0] = vec2(0.83,0.75);
    point[1] = vec2(0.60,0.07);
    point[2] = vec2(0.28,0.64);
    point[3] =  vec2(0.31,0.26);
    point[4] =  vec2(0.7,0.3);
    point[5] =  vec2(0.2,0.3);
    point[6] =  vec2(1.1,0.9); 
    point[7] =  vec2(0.5,0.4);
    point[8] =  vec2(0.9,0.9);
    point[9] =  vec2(0.9,0.1);
    point[10] =  vec2(0.5,0.8);

    point[11] =  vec2(1.3,0.2);
    point[12] =  vec2(1.7,0.5);
    point[13] =  vec2(0.91,0.76);
    point[14] =  vec2(0.6,0.6);
    point[15] =  vec2(1.4,0.7);
    point[16] =  vec2(1.6,0.1); 
    point[17] =  vec2(1.8,0.03);
    point[18] =  vec2(0.3,0.7);
    point[19] =  vec2(1.2,0.4);
    point[20] =  vec2(0.5,0.1);


    float m_dist = iTime * 0.1;
    for (int i = 0; i < 20; i++) {
        float dist = distance(st, point[i]) + float(i) * 0.02;
        if ( dist < m_dist ) {
            m_dist = dist;
        }
    }

    color += pow(fract(m_dist * 12. - velocity), 3.) * 0.4;

    fragColor = vec4(0.9, 0.4 + color.x, color.x * 2.1,1.0);
}
