void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = ((fragCoord-.5*iResolution.xy)/iResolution.y*1.8);
    vec2 ab = uv;
    vec2 aibi;
    for(int i = 0; i<100; i++){
        aibi.x = ab.x*ab.x-ab.y*ab.y;
        aibi.y = ab.x*ab.y*2.0;
        ab = aibi+uv;
    }
    if (mod(round(aibi.x*50.0) + mod(round(aibi.y*50.0), 2.0), 2.0)==1.0){
        fragColor=vec4(1,1,1,1);
    }
    else{
        fragColor=vec4(0,0,0,1);
    }
}
