float shadow(float time,vec2 uv)
{


    float velx=3.0;
    float freqx=2.0;
    float vely=3.0;
    float freqy=2.0;
    float timeshiftx=(iTime*velx)+(uv.x*freqx);
    float timeshifty=(iTime*vely)+(uv.y*freqy);
    float shadow=0.7+cos(timeshiftx)*sin(timeshifty)*0.3;
    return shadow;

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    float wiggle = sin(iTime*5.4+uv.x)*-0.04;
    // Time varying pixel color
    //vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));
    float x= length(vec2(uv.x*1.667, uv.y) - vec2(0.83, 0.5 +wiggle));
    // Output to screen
    //fragColor = vec4(col,1.0);
    if(uv.y>0.0)
    {

    fragColor = vec4(0,0.6,1,1)*shadow(iTime,uv);
    }
    if(uv.y>0.3+wiggle && uv.y<0.7 +wiggle)
    {

          fragColor = vec4(1,1,1,1)*shadow(iTime,uv);
    }
    if(x<0.13)
    {

    fragColor = vec4(1.0,0.75, 0, 1.0)*shadow(iTime,uv);
    }

}
