#define R iResolution.xy
void mainImage( out vec4 O, in vec2 I )
{
    vec2 uv = I/R;
    uv.y -= .1;
    float y = smoothstep(0.2, 1., (1.-uv.y)) * 0.08;
    float t = sin(4.*iTime+sin(uv.x*10.1451+cos(iTime*2.451+uv.x*4.)));
    uv -= .5;
    uv *= (1. - smoothstep(1., 1.5, (1.-uv.y)) * t * 0.15);
    uv += .5;
    float r = texture(iChannel0, uv + vec2(t * 0.005, y)).r;
    float g = texture(iChannel0, uv).r;
    float b = texture(iChannel0, uv - vec2(t * 0.005, y)).r;
    vec3 col = vec3(r,g,b);
    O = vec4(col,1.0);
}

#define R iResolution.xy
void mainImage( out vec4 O, in vec2 I )
{
    vec2 uv = I/R;
    vec2 st = (I-.5*R.xy)/R.y;
    float c = smoothstep(0., (-30. + (1. - uv.y) * 200.)/R.y, length(st) - .3);
    float t = sin(4.*iTime+sin(uv.x*1.425+cos(iTime*2.425+uv.x*4.)));
    c += 1. - smoothstep(0., (-30. + (1. - uv.y) * 200.)/R.y, length(st) - .1 - t * 0.01);
    vec3 col = vec3(c);
    O = vec4(col,1.0);
}
