// variant of https://shadertoy.com/view/ftyyWh

void mainImage( out vec4 O, vec2 u )
{
    vec2 R = iResolution.xy,
         U = 3.* ( 2.*u - R ) / R.y;
    U.y = fract(U.y) 
        - .02* sign( (mod(U.y,2.)-1.) ) * sin(12.56*U.x + 6.*iMouse.y/R.y);

    O = vec4( mix(.5, step(fract(U.x),.5), smoothstep( 9./R.y, 0., abs(U.y-.5)-.01 ) ));
}
