vec3 color1 = vec3(1,0,0);
vec3 color2 = vec3(0,1,0);

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
	uv-=.5;
	uv *= 6.0;  

    // Time varying pixel color
    float t = iTime+ length(uv)*5.;
    vec3 col = 0.5 + 0.5*cos(t + vec3(0,2,4));

    col = mix(color1, color2, sin(t) * .5 + .5);
    col.r = smoothstep(col.r, col.r * .9, length(fract(uv)));
    col.g = smoothstep(col.g, col.g * .8, length(fract(uv)));
    col.b = smoothstep(col.b*.5, col.b, sin(t*.5));
    // Output to screen
    fragColor = vec4(col,1.0);
}
