vec3 randomColor (vec2 st) {
    float randNum1 = fract(sin(dot(st.xy, vec2(12.9898,78.233)))*43758.5453123);
    float randNum2 = fract(cos(dot(st.xy, vec2(12.9898,78.233)))*43758.5453123);
    return vec3(randNum1, randNum2, 1.-randNum1);
}

vec2 dfd(vec2 coord){
  float dx = 1./iResolution.x * 1.0;
  float dy = 1./iResolution.y * 1.0;

  vec2 uv = coord/iResolution.xy;
  uv = floor(uv * vec2(250.))/vec2(250.);
  vec2 dxUv = vec2(uv.x + dx, uv.y);
  vec2 dyUv = vec2(uv.x, uv.y + dy);

  vec4 color = texture(iChannel0, uv);
  vec4 dxColor = texture(iChannel0, dxUv);
  vec4 dyColor = texture(iChannel0, dyUv);

  float gray = length(color.rgb);
  float dxGray = length(dxColor.rgb) - gray;
  float dyGray = length(dyColor.rgb) - gray;

  return vec2(dxGray, dyGray);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    vec2 uv = fragCoord/iResolution.xy;
    vec2 dFd = dfd(fragCoord);
    vec3 rand_color = randomColor(uv);
	fragColor = step(0.04, length(dFd)) == 0. ? vec4(1.) : vec4(rand_color,1.);
}
