void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy*2.;

    float nTime=iTime*.3;

    float s = sin(nTime*.1);
    float c = cos(nTime*.2);
    uv*=mat2(c, -s, s, c); 


    float col1=pow(sin(nTime)*uv.x,2.)-(cos(nTime)*uv.y);
    float col2=pow(cos(nTime)*uv.x,2.)-(sin(nTime)*uv.y);
    float col3=pow(sin(nTime+uv.y)*uv.x,2.)-(cos(nTime)*uv.x);


    fragColor = vec4(col1,col2,col3,1.0);
}
