///
/// I respect below post. I rebuilded the post.
///  -> https://www.shadertoy.com/view/3dBXWG
///

mat2 rot(float a)
{
    float s = sin(a);
    float c = cos(a);
    return mat2(c, -s, s, c);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord * 2.0 - iResolution.xy) / iResolution.y;
    uv *= 0.4 + sin(iTime * 0.5) * 0.1;

    vec3 col = 0.5 + 0.5 * cos(iTime + uv.xyx + vec3(0, 2, 4));

    vec2 p = uv + uv * sin(iTime) * 0.04;
    p *= rot(iTime * 0.1);

    for (float i = 0.0; i < 6.0; i++)
    {
        // -1 ~ 1 to 0 ~ 1 + offset
        p = abs(2.0 * fract(p - 0.5) - 1.0);

        float t = iTime * 0.5;
        mat2 mat = rot(p.y * sin(t));
        p *= mat;

        col += exp(-abs(p.x) * 15.0);
    }

    fragColor = vec4(col, 1.0);
}
