#define R iResolution
#define time iTime
#define audio iChannel0
#define coord 0.5
#define K 20.0

// audio
#define wav(t) texture(audio, vec2(t, 0.5)).x
#define amp(t) texture(audio, vec2(t, 0.5)).y

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {

  vec2 uv = (fragCoord.xy - R.xy*.5 ) / R.y ;

  float t = time * 0.01,
        w = wav(uv.x),
        c = sin( w * 6. +  t ) * .5;

  for( float f = -.5; f < .5; f += .05){

    float d1 = (.015 + sin( (t+f*2.)* wav(uv.x) ) * 5e-3 );

    vec2 k1 = vec2(uv.x + mod(uv.y*uv.x,.001), sin(uv.x*2.+uv.y*5.+t)*.5);

    vec2 k2 = vec2( sin( uv.x + f ) * .5, f);

    float d2 = distance(k1, k2);

    c +=  min( d1/d2, .15 );

    float acc = (.015 + sin((t+f*2.)*3.) *.015 ) / fract( K * wav(uv.x) * d2);

    c += acc * uv.y *.5;
  }

  c += 1e-3 * mod( t + K / length( uv ), 0.15);

  fragColor = vec4(c, 0., 0., 1.0);
}
