#define float2 vec2
#define float3 vec3
#define float4 vec4
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    vec2 cb06 = vec2(1.28, 0.991);
    vec3 cb07 = vec3(0.004, 0.978, 1.02249);
	vec2 cb08 = vec2(0.996847, 0.994405);
    vec2 cb09 = vec2(0.0015, 0.002667);

    vec2 v1 = uv;
    vec2 uv2;

    vec4 r0;
	r0.xy = v1.xy*float2(2.0, 2.0) + float2(-1.0, -1.0);
	r0.xy = r0.xy * cb08.xy;
	r0.zw = r0.xy*float2(0.5, 0.5) + float2(0.5, 0.5);
	r0.xy = r0.xy * float2(0.5, 0.5);
	r0.xy = r0.xy * cb09.xy;
	r0.xy = r0.xy + r0.xy;
	r0.zw = r0.zw*float2(2.0, 2.0) + float2(-1.0, -1.0);
	r0.zw = r0.zw * cb07.zz;

    fragColor = r0;

	float4 r1;
	r1.x = dot(r0.zwzz, r0.zwzz);
	r1.x = sqrt(r1.x);
	r1.x = max(r1.x, 0.0);
	r1.x = log2(r1.x);
	r1.y = cb07.x * 5.0;
	r1.x = r1.x * r1.y;
	r1.x = exp2(r1.x);

    fragColor = r1;

    r0.zw = r0.zw * r1.xx;
    r0.zw = r0.zw * cb07.yy;
    r0.zw = r0.zw*vec2(0.5, 0.5) + vec2(0.5, 0.5);
    r0.zw = r0.zw-uv.xy;
    r0.zw = r0.zw + vec2(0.0118, 0.0118);

    vec4 col;
    col.xy = clamp(r0.zw * vec2(42.5, 42.5), 0.0, 1.0);

    col.z = dot(r0.xyxx, r0.xyxx);
    r0.z = sqrt(r0.z);
    r0.z = max(r0.z, 0.0);
    r0.xy = r0.xy/r0.zz;
    r0.z = log2(r0.z);
    r0.z = r0.z * cb06.y;
    r0.z = exp2(r0.z);
    r0.xy = r0.zz*r0.xy + vec2(0.0059, 0.0059);
    col.zw = clamp(r0.xy * vec2(85.0, 85.0), 0.0, 1.0);

    // Output to screen
    fragColor = col;
}
