vec3 srgb2lin( vec3 cs )
{
	vec3 c_lo = cs / 12.92;
	vec3 c_hi = pow( (cs + 0.055) / 1.055, vec3(2.4) );
	vec3 s = step(vec3(0.04045), cs);
	return mix( c_lo, c_hi, s );
}
vec3 lin2srgb( vec3 cl )
{
	vec3 c_lo = 12.92 * cl;
	vec3 c_hi = 1.055 * pow(cl,vec3(0.41666)) - 0.055;
	vec3 s = step( vec3(0.0031308), cl);
	return mix( c_lo, c_hi, s );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //left, (0, 1) checkerboard pattern. averages on the retina to MAX_LUMINANCE/2.0 
    if(gl_FragCoord.x/iResolution.x < .5){
    	fragColor = vec4(mod(gl_FragCoord.x + gl_FragCoord.y, 2.0));
    }
    else{
        //top right 'should' blend in with the left hand side if you squint
        fragColor = vec4( 1.0 / 2.0 ); //my intent is to display MAX_LUMINANCE/2.0 here, doesnt happen. 
        if(gl_FragCoord.y/iResolution.y < .5){
            //bottom right. sadly does blend in with the left hand side better, cant know the display properties until we measure it
        	fragColor.xyz = lin2srgb(fragColor.xyz);
        }
    }
}
