float circlemask(vec2 uv, vec2 ctr, float ry, float size) {
	float pct = 0.;
    pct = distance(uv, ctr);
    pct = smoothstep(size, size + 3./ry, pct);
    return pct;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 R = iResolution.xy;
    vec2 uv = (2. * fragCoord - R) / R.y;

	float pct = 0.;

    pct += circlemask(uv, vec2(0.25), R.y, 0.25);
    pct -= circlemask(uv, vec2(0.), R.y, 0.25);

    vec3 col = vec3(pct);

    fragColor = vec4(col,1.0);
}
