#define PI 3.14159
#define TAU PI*2.


float cubicPulse(float c, float w, float x )
{
    x = abs(x - c);
    if( x>w ) return 0.0;
    x /= w;
    return 1.0 - x*x*(3.0-2.0*x);
}

float debugCrossFade(vec2 uv, float timeA, float timeB, float timeCrossFaded){
    float pulseA = cubicPulse(timeA, 0.1, uv.y);
    float pulseB = cubicPulse(timeB, 0.1, uv.y);
    float fadedValue = mix(pulseA, pulseB, timeCrossFaded);
    return fadedValue;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    float timeSpeed = 0.15;
    float timeA = fract(iTime * timeSpeed);
    float timeB = fract(iTime * timeSpeed + 0.5);
    float timeCrossFaded = .5+.5*sin(PI * .5 + TAU * iTime * timeSpeed);
    vec2 flowVector = 0.05*texture(iChannel0, uv).rb;
    vec2 offsetA = flowVector*timeA;
    vec2 offsetB = flowVector*timeB;
    vec3 sampleA = texture(iChannel1, uv+offsetA).rgb;
    vec3 sampleB = texture(iChannel1, uv+offsetB).rgb;
    vec3 sampleCrossFaded = mix(sampleA, sampleB, timeCrossFaded);
    fragColor = vec4(sampleCrossFaded, 1.);
   // fragColor = vec4(vec3(debugCrossFade(uv, timeA, timeB, timeCrossFaded)), 1.);    
}
