// Tuned via NoodlesPlate (https://github.com/aiekick/NoodlesPlate/releases)

#define AA 2
//#define ANIMATED

vec2 warp(vec2 p)
{
	p *= 2.7;

	for (int i = 0; i < 6; i++)
	{
		float a = atan(p.x,p.y) * 4.0;
		float l = length(p);
		p = abs(p);
#ifdef ANIMATED
        p -= (sin(iTime-1.7)*0.5+0.5)*0.5;
#endif
		p += vec2(cos(a),sin(a)) * l * p.yx;
	}

	if (dot(p,p) < 0.)
		p = abs(fract(p) - 0.5);

	return p;
}

float shape(vec2 p)
{
	p = warp(p);

	return length(p);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = vec4(0);

    int _AA = max(AA,1); // avoid div by 0 at line 69

	for(int i=0;i<_AA;i++)
    for(int j=0;j<_AA;j++)
    {
        vec2 uv = ((fragCoord + vec2(i,j)) * 2.0 - iResolution.xy)/iResolution.y;

        float a = -1.2;
        uv *= mat2(cos(a),-sin(a),sin(a),cos(a));

        float k = 0.04;
        float f = shape(uv);
        float fx = shape(uv + vec2(k,0.0))-f;
        float fy = shape(uv + vec2(0.0,k))-f;

        vec3 n = normalize(vec3(fx,0.1,fy));

        float r = abs(f-0.8);
        if ( r < 1.0)
        {
            vec3 col = mix(vec3(0.2,1,0),vec3(0), r);
            col += pow(abs(n), vec3(5.0));
            fragColor.rgb += col;
        }
        else
        {
            fragColor.rgb += vec3(0.0);
        }
    }

    fragColor.rgb /= float(_AA * _AA);
	fragColor.a = 1.0;
}
