float DrawPoint(vec2 uv,vec2 p, float r)
{
    return smoothstep(abs(r)+0.000001,abs(r),length(uv-p));
}

float H21(vec2 uv)
{
    uv = vec2(uv.x*25.64f,uv.y*651.1f);
    uv += dot(uv,uv+2.34f);
    return fract(uv.x+uv.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = (fragCoord-0.5f*iResolution.xy)/iResolution.y;

    vec3 col = vec3(0.0f);
    float p = 0.0f;
    const float spacing = 0.1f;
    const float row = 10.0f;
    const float column = 13.0f;
    float time = iTime*0.5+26.3;
    vec2 coord;
    float r;
    float seed;
    float loop;
    float t;

    for(float j = -row; j <= row; j++){
        for(float i = -column; i <= column; i++){

            coord = vec2(i * spacing,j*spacing);
            seed = H21(coord);
            t = time-length(coord)*0.2;

            loop = mod(floor((t/6.283f)),16.) +1.0f ;
            r = mix(-0.01,0.1*loop,clamp(smoothstep(-1.0,1.0,sin(t)),0.0f,1.0f));
            p += DrawPoint(uv,coord,r * mix(1.0,1.f,H21(coord)));
        }
    }
    col+= mod(p,2.0f);
    col = fract(vec3(-p*205.061,p*1797.87f,p*18.16));
    fragColor = vec4(col,1.0);
}
