void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 st = (fragCoord-.5*iResolution.xy)/iResolution.y+.5;
    vec3 color;

    // Square 1
    vec2 sqr1 = step(vec2(0.0),st);
    float pct1 = sqr1.x * sqr1.y;

    // Square 2
    vec2 sqr2 = step(vec2(0.0),1.0-st);
    float pct2 = sqr2.x * sqr2.y;

    // Square 3
    vec2 sqr3 = step(vec2(0.5, 0.3),st);
    float pct3 = sqr3.x * sqr3.y;

    // Square 4
    vec2 sqr4 = step(vec2(0.5,0.3),1.0-st);
    float pct4 = sqr4.x * sqr4.y;

    color += vec3(pct1 * iTime) * vec3(170,0,0);
    color += vec3(pct2) * vec3(0,170,0);
    color += vec3(pct3) * vec3(170,0,50);
    color += vec3(pct4) * vec3(0,170,50);

    // Output to screen
    fragColor = vec4(color,1);
}
