float circle(vec2 uv, vec2 pos, float r, float blur)
{
    uv -= pos;
    float d = length(uv);
    float c = smoothstep(r,r-blur,d);

    return c;
}

float N21(vec2 p)
{
    return fract( sin(p.x*8787.+p.y*1732.)*57484.);
}

float smoothNoise(vec2 uv)
{
    vec2 lv = fract(uv);
    vec2 id = floor(uv);

    lv = lv*lv*(3.-2.*lv);

    float bl = N21(id);
    float br = N21(id+vec2(1,0));
    float b = mix(bl, br, lv.x);

    float tl = N21(id+vec2(0,1));
    float tr = N21(id+vec2(1,1));
    float t = mix(tl, tr, lv.x);

    return mix(b,t,lv.y);
}

float smoothNoise2(vec2 uv)
{
    float c = smoothNoise(uv*4.);
    c += smoothNoise(uv*8.)*.5;
    c += smoothNoise(uv*16.)*.25;
    c += smoothNoise(uv*32.)*.125;
    c += smoothNoise(uv*64.)*.0625;

    return c / 2.;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{



	vec2 uv = fragCoord/iResolution.xy;

    uv -= vec2(0.5);
    uv.x *= iResolution.x/iResolution.y;

    float c = 0.;

    c+= circle(uv, vec2(sin(iTime*.6)/4. + .5, mod(iTime*.5,2.)-1.), .5, .6);
    c+= circle(uv, vec2(sin(iTime*.5)/5., mod(iTime*.3,2.)-1.), .5, .6);
	c+= circle(uv, vec2(sin(iTime*.5)/5., mod(iTime*.2,1.)-1.), .5, .6);


	c = smoothstep(.5, .52, c);
	fragColor = vec4(vec3(c),1.);
}
