float width = 9.0;
float height = 9.0;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    //getting resolution ratio
    float ratio = iResolution.x/iResolution.y;

    //making the tiles quares
    uv.x *= width * ratio  ;
    uv.y *= height * width/height;


    //creating tile indices
    float tileXIndex = step( 1.00, mod(floor(uv.x),2.0));
    float tileYIndex = step( 1.00, mod(floor(uv.y),2.0));

    float tileIndex = tileXIndex + tileYIndex;

    //chooses which color to display
    if (uv.x > floor(width * ratio)){
    	fragColor = vec4(0.0,0.0,0.0,1.0);
    }
    else if(mod(tileIndex,2.0) == 1.0) {
     fragColor = texture(iChannel0, uv);   
    }
    else
        fragColor = texture(iChannel1,uv);


}
