vec2 grid = vec2(30.0,50.0);


vec4 checkerColor1 = vec4(1.0,1.0,0.0,1.0);
vec4 checkerColor2 = vec4(0.0,0.0,1.0,1.0);
vec4 blockOutColor = vec4(1.0,1.0,1.0,1.0);
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    uv*=grid;


   	float xModifier = iResolution.x/iResolution.y;
    float yModifier = iResolution.y/iResolution.x;
    float squareModifierX = grid.x/grid.y;
    float squareModifierY = grid.y/grid.x;

    if(squareModifierX < xModifier)
    {
        uv.x*=xModifier;
        uv.x/=squareModifierX;
    }
    else
    {
        uv.y*=yModifier;
        uv.y/=squareModifierY;
    }

	float overX = step(grid.x,uv.x);
    float overY = step(grid.y,uv.y);

    float outOfBounds= abs(overX-overY);

    vec4 col;

    float onoff1 = mod(floor(uv.y),2.0);
    float onoff2 = mod(floor(uv.x),2.0);
    checkerColor1.a = abs(onoff1-onoff2);

    col=mix(checkerColor1,checkerColor2,checkerColor1.a);
    col=mix(col,blockOutColor,outOfBounds);

    // Output to screen
    fragColor = col;
}
