void mainImage( out vec4 O, vec2 u )
{
    vec2 R = iResolution.xy,
         U = 15. * u /R.y,  A;

    float i = floor(U.x);                     // column id
    U.x = fract(U.x);                         // local coords
    U.y += 2.*i* iTime;                       // box i falls at speed 2i
    U.y += 15.*fract(1e4*sin(1e4*i));         // randomize col phase
    float j = floor( mod( U.y, 15.) *4./15.), // row
          r = mod(j,2.)<1. ? .4 : .1;         // box radius
    U.y = mod( U.y, 15./4. );               

    A =  15./R.yy;                            // spatial AA
    if (iMouse.z<=0.)
        A += vec2( 0, 2.*i * iTimeDelta );    // temporal AA

    U = abs(U-vec2(.5,2.5));                  // --- renders AAÂ box
    U = smoothstep( .7*A, -.7*A, U - r );
    O = vec4( U.x*U.y ); // vec4( min(U.x,U.y) );

    O = pow(O, vec4(1./2.2) );                // to sRGB
}
