mat2 rotate(float a)
{
	float c = cos(a);
    float s = sin(a);
    return mat2(c, s, -s, c);
}

float smu( float d1, float d2, float k ) {
    float h = clamp( 0.5 + 0.5*(d2-d1)/k, 0.0, 1.0 );
    return mix( d2, d1, h ) - k*h*(1.0-h);
}

float map(vec3 p)
{
    p.xz *= rotate(iTime);
    p += .5;
    float s =100.;

    //p = abs(p);
    for(int i=0; i<15; ++i) {
        p -= .2;
        p.xz *= rotate(sin(iTime*1.4));
        p.yz *= rotate(cos(iTime*2.));
        //s = min(s, length(p)-sqrt(float(i))*.2);
        s = smu(s, length(p)-sqrt(float(i))*.015, 1.);
    }

    return s;
}

float march(vec3 ro, vec3 rd)
{
    float t = 0.;
    for(int i=0; i<128; ++i) {
        float d = map(ro+rd*t);
        if(d < .001) break;
        t += d;
    }
    return t;
}

vec3 getNormal(vec3 p)
{
    vec3 eps = vec3(.001, 0., 0.);
    return normalize(vec3(
    	map(p+eps.xyy) - map(p-eps.xyy),
        map(p+eps.yxy) - map(p-eps.yxy),
        map(p+eps.yyx) - map(p-eps.yyx)
    ));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy - vec2(.5);
    uv.x *= iResolution.x/iResolution.y;

    vec3 eye = vec3(0., 0., 15.);
    vec3 dir = normalize(vec3(uv.x, uv.y, -1.));
    float d = march(eye, dir);
    vec3 p = eye+dir*d;
    vec3 normal = getNormal(p);
    vec3 col = normal;

    // Output to screen
    fragColor = vec4(col,1.0);
}
