void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	float speed = 100.0;
    float zoom = 0.1 * (1.0 - mod(iTime, speed)/speed);
    vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 originaluv = uv;

    uv -= 0.56 * mod(iTime, speed);
    uv /= mod(iTime, speed);

    float xshifted = (uv.x - 0.5) * 512.0;

    float colorshift = texelFetch(iChannel0, ivec2(uv.x,0), 0).x;

    float startx = colorshift * zoom + mod(iTime, 10.0)/100.0;

    vec2 z = vec2(startx, -startx);

    float count = 0.0;

    for(int i = 0; i < 512; i++)
    {
        z = vec2(z.x*z.x - z.y*z.y, 2.0*z.x*z.y) + uv;
        if(dot(z,z) > 1024.0){break;}
        count += 1.0;
    }

    float countF = float(count);

    float color = countF / 512.0;
    color = 1.0 - color;

    vec3 texColor = texelFetch(iChannel1,ivec2(originaluv.x, originaluv.y), 0).rgb;

    fragColor = vec4(vec3(colorshift* sin(iTime), color/2.0, 1.0 - colorshift/2.0) + texColor, (1.0));

}
