void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    float nbLigne = 30.0;
    float nbcol = 10.0;
    // Time varying pixel color
   float resY =floor(mod(uv.y,nbLigne)*nbLigne);

    float decal = mod(resY,2.0);
 	resY = resY/nbLigne;

    if( decal == 1.0) uv.x += 0.5/nbcol;


     float resX =floor(mod(uv.x,nbcol)*nbcol);
    resX = resX/nbcol;

    float res = (resX+resY) * 0.5;
    // Output to screen
    fragColor = vec4(res,res,res
                     ,1.0);
}
