#define PI 3.14159265
#define DPI 6.2831853


float circle(float r, in vec2 uv, in vec2 pos){

    uv = uv-pos;
    return 1.0-step(r,length(uv));

}
float triangle(in vec2 uv, float n, float tam, in vec2 pos ){

    uv = uv-pos;
    float a = atan(uv.x, uv.y) - PI;
    float r = DPI/n;
    float d = cos(floor(0.5+a/r)*r-a)*length(uv);
    d = 1.0-step(tam,d); 
    return d;
}

float triangle2(in vec2 uv, float n, float tam, in vec2 pos ){

    uv = uv-pos;
    float a = atan(uv.x, uv.y) - PI + 10.*sin(iTime/3.);
    float r = DPI/n;
    float d = cos(floor(0.5+a/r)*r-a)*length(uv);
    d = 1.0-step(tam,d); 
    return d;
}	

float triangle3(in vec2 uv, float n, float tam, in vec2 pos ){

    uv = uv-pos;
    float a = atan(uv.x, uv.y) - PI;
    float r = DPI/n;
    float d = cos(floor(0.5+a/r)*r-a)*length(uv);
    d = 1.0-step(tam,d); 
    return d;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 p = -1.0 + 2.0*uv;
    p.x *= iResolution.x/iResolution.y;

    vec3 col = vec3(0.0);


    //Triangle
    float x0 = triangle(p, 4.0, .1, vec2(cos(5.*iTime),sin(5.*iTime)));
    float c0 = circle(0.2, p, vec2(-1.2, 0.0 + .75*sin(5.*iTime)));
    float c1 = circle(0.2, p, vec2(0. + .75*sin(5.*iTime) , -0.5));

	float x1 = triangle3(p, 6.0, abs(sin(5.*iTime)), vec2(.0,.0));

	float x2 = triangle2(p, 3.0, .2, vec2(.0,.0));

    col += vec3(c0)*vec3(0.2,0.8,1.);
    col += vec3(c1)*vec3(0.2,0.8,1.);

    col += vec3(x2)*vec3(1.,0.,.0);
    col += vec3(x0)*vec3(0.,0.,1.0);
    col += vec3(x1)*vec3(0.,1.,.0);

	fragColor = vec4(col,1.);
}
