void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = fragCoord / iResolution.xy;
    if (uv.y < .333) {
        float offset = sin(uv.x * 10. + sin(uv.y * 20. + iTime));
        uv.y = .666 - uv.y + offset * .015;
        fragColor = vec4(textureLod(iChannel0, uv, 1.5).rgb * vec3(.85, .9, .95) - .05, 1.);
    } else
        fragColor = textureLod(iChannel0, uv, 0.);
}
