void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    float angle =  iTime / 2. + uv.y;
    float intensity = 0.5 + 0.5*sin(cos(angle)*fragCoord.x/10.0 + sin(angle)*fragCoord.y/10.0);

    // Output to screen
    fragColor = vec4(intensity*vec3(1.0,0.0,1.0),1.0);
}
