void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    float amplitutde = texture(iChannel0, vec2(uv.x, 0)).r;

    vec3 col = vec3(1.);
    col *= 1. - smoothstep(0.01, 0.015, length(amplitutde - uv.y));


    float bars = 10.;
    float margin = 0.01;
    float barWidth = (uv.x - margin * (bars + 1.)) / bars;

    // which bar am I in?
    int myBar = int(floor(uv.x / bars));    

    vec2 samplePos = vec2(float(myBar)*float(textureSize(iChannel0, 0).x)/bars, 0.);
    float amplitude = float(texture(iChannel0, samplePos));

    fragColor = vec4(col, 1.);
}
