// phong shading
vec3 p_shading( vec3 v, vec3 n, vec3 eye, vec3 pcol ) {
	// ...add lights here...

	float shininess = 16.0;

	vec3 final = vec3( 0.0 );

	vec3 ev = normalize( v - eye );
	vec3 ref_ev = reflect( ev, n );

	// light 0
	{

		vec3 vl = normalize( light_pos1 - v );

		float diffuse  = max( 0.0, dot( vl, n ) );
		float specular = max( 0.0, dot( vl, ref_ev ) );
		specular = pow( specular, shininess );

		final += light_color1 * ( diffuse + specular ); 
	}

	// light 1
	{

		vec3 vl = normalize( light_pos2 - v );

		float diffuse  = max( 0.0, dot( vl, n ) );
		float specular = max( 0.0, dot( vl, ref_ev ) );
		specular = pow( specular, shininess );

		final += light_color2 * ( diffuse + specular ); 
	}

    return (final)*pcol/1.5;
}

