vec4 distortp(vec2 uv, float add) {

    float dist = (0.01+0.01*sin(round(iTime)+add))*sin(add+iTime+round((-0.5+uv.y)*(45.0+30.0*sin(-0.25+uv.y+iTime*0.1))));
    uv.x += dist;
    return texture( iChannel0, uv ) * (1.0+dist*10.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec2 c = vec2(uv.x-0.5, uv.y-0.5);

    float d = sqrt(c.x*c.x+c.y*c.y); d*=d*d*d;



    fragColor = distortp(vec2(uv.x,uv.y),0.0)*(1.5-10.0*d);
}
