float random (in vec2 _st) {
    return fract(sin(dot(_st.xy, vec2(0.890,-0.900)))*757.153);
}

// Based on Morgan McGuire @morgan3d
// https://www.shadertoy.com/view/4dS3Wd
float noise (in vec2 _st) {
    vec2 i = floor(_st);
    vec2 f = fract(_st);

    // Four corners in 2D of a tile
    float a = random(i);
    float b = random(i + vec2(1.0, 0.0));
    float c = random(i + vec2(0.0, 1.0));
    float d = random(i + vec2(1.0, 1.0));

    vec2 u = f * f * (3.0 - 2.0 * f);

    return mix(a, b, u.x) + (c - a)* u.y * (1.0 - u.x) + (d - b) * u.x * u.y;
}

float fbm ( in vec2 _st) {
    float v = sin(iTime*0.3)*0.1;
    float a = 0.1;
    vec2 shift = vec2(100.0);
    // Rotate to reduce axial bias
    mat2 rot = mat2(cos(0.5), sin(1.0), -sin(0.5), acos(0.5));
    for (int i = 0; i < 3; ++i) {
        v += a * noise(_st);
        _st = rot * _st * 2.0 + shift;
        a *= 2.6;
    }
    return v;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 st = (2.*fragCoord-iResolution.xy)/min(iResolution.x,iResolution.y)*0.1;

    vec3 color = vec3(0.);

    vec2 q = vec2(0.);
    q.x = fbm( st + 1.0);
    q.y = fbm( st + vec2(-0.450,0.650));

    vec2 r = vec2(0.);
    r.x = fbm( st  + vec2(0.570,0.520)+ 0.02*iTime );
    r.y = fbm( st  + vec2(0.340,-0.570)+ 0.05*iTime);

    for (float i = 0.0; i < 3.0; i++) {
        r += 1.0 / abs(mod(st.xy, 0.035* i) * 10000.) * 1.;//Virtical line
        r += 1.0 / abs(mod(st.y + st.x, 1.0 * i) * 10000.) * 1.;//Diagonal line
    }

    color = mix(vec3(0.730,0.513,0.302), vec3(0.397,0.576,0.667), sin(iTime*0.2));

    float f = mix(fbm(st+r),fbm(st+r*0.15),step(0.,st.x+st.y));//left metal, right glass

    color = 2.0*(f*f*f+.6*f*f+.5*f)*color;

    fragColor = vec4(color,1.);
}
