void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    float offset=0.01;
    vec2 ro = vec2(offset,0);
    vec2 go = vec2(0,0);
    vec2 bo = vec2(-offset,0);

    float r=texture(iChannel0,uv-ro).x;
    float g=texture(iChannel0,uv-go).y;
    float b=texture(iChannel0,uv-bo).z;

    fragColor = vec4(r,g,b,1.0);
}

vec2 mainSound( int samp, float time )
{
        float A=(200.)*6.2831*time;
        float s=1.05946309;
        vec2 chord=vec2(sin(A*pow(s,0.))+sin(A*pow(s,5.))+sin(A*pow(s,10.))+sin(A*pow(s,15.)))/5.;
        return chord*time*exp(-1.*time)*sin(17.*time);
}
