void mainImage( out vec4 O, vec2 U )
{
    vec2 R = iResolution.xy,
         M = iMouse.xy;
    if ( length(M)<20. ) M = floor( R/2. + R/3.*cos( floor(iTime*4.)*vec2(1,1.7) + vec2(0,33) ) );
                              // floor is crucial 

    O = vec4( fract(sin(dot( U, M )) * 1e5 ) ) ; // 43758.5453 ) );
    if ( length(M-U) < 10. ) O = vec4(1,0,0,1);
}
