float discSDF(vec2 p, float r) {
    return length(p) - r;
}

float disc(vec2 p, float r) {
 	return step(.0, discSDF(p,r));   
}


float funkyDisc(vec2 p, float r) {
    // compute the angle to add some noise to the func
    return 0.;
}



float circle(vec2 p, float r, float w) {
    float d = discSDF(p, r);
    float start = d + (w / 2.);
    float end = d - (w / 2.);
    float afterStart = step(.0, start); // 1  if after the start
    float afterEnd = step(.0, -end); // 1 if before end
    return step(1.99, afterStart + afterEnd);    
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    float radius = min(iResolution.x, iResolution.y) / 5.;
    vec2 center = iResolution.xy / 2.;

    vec2 v = fragCoord - center;    
    float c = circle(v, radius, 4.);
//    float c = disc(v, radius);


    fragColor = vec4(c,c,c,1.0);    
}
