#define cdist 3.0
float circle(vec2 uv, vec2 pos, float r)
{
    uv = uv - pos;
    float x = dot(uv, uv);
	return (x < (r * r)) ? 1.0 : 0.0;
}
float hyperbolic(vec2 uv, vec2 pos)
{
    float len = length(pos) + cdist*abs(1.5+sin(iTime));
	vec2 n = pos/len;
    float da = 1.0/(2.0 + len);
    vec2 nu = n * (1.0 - da);
    return 2.0*circle(uv, nu, da * da);
}
float intersect(vec2 uv)
{
    vec2 mouse = (-1.0 + 2.0*iMouse.xy / iResolution.xy) * vec2(iResolution.x/iResolution.y, 1.0);
    mouse *= 5.0;
    float sum = 0.0;
    for(float x = -20.; x < 20.; ++x)
        for(float y = -20.; y < 20.; ++y)
			sum += hyperbolic(uv, vec2(x, y) + mouse);
	return sum;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (-1.0 + 2.0*fragCoord.xy / iResolution.xy) * vec2(iResolution.x/iResolution.y, 1.0);
	float l = intersect(uv);
    float r = circle(uv, vec2(0.0), 1.0);
    //(0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4)))
    // Time varying pixel color
    vec3 col = vec3(0.1, r, 0.1) + vec3(0.5 * l);

    // Output to screen
    fragColor = vec4(col,1.0);
}
