void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec4 rawColor = texture(iChannel0, uv);

    vec4 left = texture(iChannel0, uv + vec2(-0.01, 0.0));
    vec4 right = texture(iChannel0, uv + vec2(0.01, 0.0));
    vec4 top = texture(iChannel0, uv + vec2(0.0, -0.01));
    vec4 bottom = texture(iChannel0, uv + vec2(0.0, 0.01));
    vec4 averageColor = (left + right + top + bottom) / 4.0;

    // Output to screen
   	fragColor = rawColor + (rawColor - averageColor) * (sin(iTime) + 1.0);
}
