/**
 * SDF's from:
 * https://iquilezles.org/articles/distfunctions
 **/

float sd_sphere(vec3 p, float r) {
	return length(p) - r;
}
float sd_box(vec3 p, vec3 s) {
	vec3 d = abs(p) - s;
	return length(max(d, 0.0)) + min(max(d.x, max(d.y, d.z)), 0.0);
}
float sd_box(vec3 p, vec3 s, float r) {
	vec3 d = abs(p) - s;
	return length(max(d, 0.0)) - r + min(max(d.x, max(d.y, d.z)), 0.0);
}
float sd_torus(vec3 p, vec2 s) {
	return length(vec2(length(p.xz) - s.x, p.y)) - s.y;
}
float sd_cylinder(vec3 p, vec3 s) {
	return length(p.xz - s.xy) - s.z;
}
float sd_octahedron(vec3 p, float s) {
  p = abs(p);
  return (p.x + p.y + p.z - s) * 0.57735027;
}

mat2 op_rotate(float a) {
	float s = sin(a);
	float c = cos(a);
	return mat2(c, -s, s, c);
}
vec3 op_repeat(vec3 p, vec3 c) {
	return mod(p, c) - 0.5 * c;
}
vec3 op_twist(vec3 p, float k)
{
	float c = cos(k * p.y);
	float s = sin(k * p.y);
	mat2  m = mat2(c, -s, s, c);
	return vec3(m*p.xz, p.y);
}
vec3 op_bend(vec3 p, float k)
{
	float c = cos(k * p.x);
	float s = sin(k * p.x);
	mat2  m = mat2(c, -s, s, c);
	return vec3(m*p.xy, p.z);
}


// https://iquilezles.org/articles/smin
float smin(float a, float b, float k)
{
    float h = max(k - abs(a - b), 0.0);
    return min(a, b) - h * h * 0.25 / k;
}

// https://iquilezles.org/articles/smin
float smax(float a, float b, float k)
{
    float h = max(k - abs(a - b),0.0);
    return max(a, b) + h * h * 0.25 / k;
}

float sd_capsule(vec3 p, vec3 a, vec3 b, float r)
{
  vec3 pa = p - a, ba = b - a;
  float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
  return length( pa - ba*h ) - r;
}

vec4 op_elongate(vec3 p, vec3 h)
{
    //return vec4( p-clamp(p,-h,h), 0.0 ); // faster, but produces zero in the interior elongated box

    vec3 q = abs(p)-h;
    return vec4( max(q,0.0), min(max(q.x,max(q.y,q.z)),0.0) );
}

float map(vec3 pos)
{
    pos.xz *= op_rotate(iMouse.x / iResolution.x * 10.0);
	pos.y += (iMouse.y / iResolution.y * 4.0) - 0.9;

    float s = 0.75;

    // create floor
    float d = pos.y;

    { // Base
    vec3 p = pos - vec3(0, s, 0);
	d = min(d, sd_box(p, s * vec3(1.75, 0.20, 0.5)) - s * 0.1);
	d = min(d, sd_box(p, s * vec3(2.0, 0.20, 0.5)) - s * 0.05);

    p.x -= s * 2.15;
	d = smax(d, -sd_sphere(p, s * 0.12), 0.01);
	}

    { // Cap
    vec3 p = pos - s * vec3(-1.25, 1, 0);
	float q = sd_box(p, s * vec3(1.75, 0.20, 0.5)) - s * 0.1;

    p = pos - s * vec3(0.1, 1, 0);
    q = max(q, -sd_box(p, s * vec3(2, 1, 1)));
    d = min(d, q);
    }

    { // Clip
        vec3 p = pos - s * vec3(-1.3, 1.5, 0);
	    d = min(d, sd_box(p, s * vec3(0.9, 0.06, 0.25)) - s * 0.02);
        p -= s * vec3(-1.3, -0.08, 0.0);
        p.xy *= op_rotate(-0.2);
	    d = min(d, sd_box(p, s * vec3(0.4,0.06, 0.25)) - s * 0.02);

    }

    return d;
}


vec3 calc_normal(vec3 p)
{
    vec2 e = vec2(0.0001, 0.0);
    return normalize(vec3(map(p + e.xyy) - map(p - e.xyy),
                          map(p + e.yxy) - map(p - e.yxy),
                          map(p + e.yyx) - map(p - e.yyx)));
}
float cast_ray(vec3 ro, vec3 rd)
{
    float total = 0.0;
    for (int i = 0; i < 100; ++i) {
        vec3 pos = ro + total * rd;

        float dist = map(pos);

        if (dist < 0.001) break; // to close
        total += dist;
        if (total > 20.0) { // to far
            total *= -1.0;
            break;
        }
    }
    return total;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from -1 to 1)
    fragCoord *= 2.0;
	vec2 p = (fragCoord - iResolution.xy ) / iResolution.y;

    vec3 ro = vec3(0.0, 1.0, -3.0); // ray origin
    vec3 rd = normalize(vec3(p, 1.5)); // ray direction

    vec3 col = vec3(0.6, 0.7, 0.8) - 0.4 * rd.y; // sky
    col = mix(col, vec3(0.75, 0.8, 0.85), exp(-13.0 * rd.y)); // horizon

    float total = cast_ray(ro, rd);

    if (total > 0.0) {
        vec3 pos = ro + total * rd;
        vec3 norm = calc_normal(pos);

        vec3 mate = vec3(0.18); // Don't do full white's

        // dot(a, b) -> how aligned are a and b
        vec3 sun = normalize(vec3(0.8, 0.4, 0.2));
        float sun_dif = clamp(dot(norm, sun), 0.0, 1.0);
        // step(a, b) -> if a < b then 0 else 1
        float sun_sha = step(cast_ray(pos + norm * 0.001, sun), 0.0);
        col = mate * vec3(7.0, 4.5, 3.0) * sun_dif * sun_sha;


        // Blueish sky light from above
        float sky_dif = clamp(0.5 + 0.5 * dot(norm, vec3(0, 1, 0)), 0.0, 1.0);
        col += mate * vec3(0.5, 0.8, 0.9) * sky_dif;

        // Remove blacks, by bouncing light of the ground
        float bou_dif = clamp(0.5 + 0.5 * dot(norm, vec3(0, -1, 0)), 0.0, 1.0);
        col += mate * vec3(0.7, 0.3, 0.2) * bou_dif;
    }
    col = pow(col, vec3(0.4545)); // Gamma correction

    fragColor = vec4(col, 1.0);
}
