void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 col;
	float t = iTime*.1;
    vec2 uv = (fragCoord-iResolution.xy)/iResolution.y+vec2(t,t*2.0);
    float factor = 1.5;
    vec2 v1;
    for(int i=0;i<12;i++)
    {
        uv *= -factor*factor;
        v1 = uv.yx/factor;
        uv += sin(v1+col)/factor;
        col += vec2(sin(uv.x-uv.y+v1.y-col.y),sin(uv.y-uv.x+v1.x-col.x));
    }
    fragColor = vec4(vec3(col.x+4.0,col.x-col.y/2.0,col.x/5.0)/4.0,1.0);
}

// http://countercomplex.blogspot.jp/2011/10/algorithmic-symphonies-from-one-line-of.html
// http://wurstcaptures.untergrund.net/music/

vec2 sound1(int t)
{
    t = (t>>(t/((t/4096)%5+(t/4096)%3)))|t>>4&t>>5|t>>6;
    return vec2(float(t & 0xff - 128) / 128.);
}

vec2 mainSound( in int samp,float time){
    time *= 16000.0;
    float factor = .5;
    vec2 result = vec2(0.0);
    for(int i = 0; i < 3; i++){
    	result += sound1(int(time*factor))*factor;
        factor *= 1.5;
    }
    return result/100.0;
}
