void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    float roundness = 0.1; //edit this to change roundness

    vec2 temp = abs((uv - vec2(0.5)) * vec2(2.0));
    if(temp.x > 1.0 - roundness && temp.y > 1.0 - roundness) {
        if(distance(vec2(1.0 - roundness), temp) > roundness) {
            fragColor = vec4(0.0);
            return;
        }
    }

    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    fragColor = vec4(col, 1.0);
}
