vec3 colorA = vec3(1.0,94.0/255.0,69.0/255.0);
vec3 colorB = vec3(221.0/255.0,178.0/255.0,133.0/255.0);
vec3 colorC = vec3(192.0/255.0,193.0/255.0,197.0/255.0);
vec3 colorD = vec3(55.0/255.0,125.0/255.0,193.0/255.0);
vec3 colorE = vec3(33.0/255.0,68.0/255.0,98.0/255.0);


void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 st = fragCoord/iResolution.xy;

    float offset = sin(iTime/10.0+st.x)/4.0;

    vec3 color1 = mix(colorA,colorB,smoothstep(0.0, 0.25+offset, st.y));
    color1 = mix(color1,colorC,smoothstep(0.25+offset, 0.33+offset, st.y));
    color1 = mix(color1,colorD,smoothstep(0.33+offset, 0.66+offset, st.y));
    color1 = mix(color1,colorE,smoothstep(0.66+offset, 1.00, st.y));


    fragColor = vec4(color1,1.0);
}
