void mainImage( out vec4 Q, in vec2 U )
{
    Q = A(U);
}

void mainImage( out vec4 Q, in vec2 U )
{
    U.x = abs(U.x-0.5*R.x)+0.5*R.x;
    Q = A(U);
    vec4 m = vec4(0);
    for(int x = -1; x<=1; x++)
    for(int y = -1; y<=1; y++) {
        vec4 a = A(U+vec2(x,y));
		m += a;
    }
    m /= m.w;
    Q += vec4(.3,1,.1,.1)*(m-Q);
    Q.x +=  .3*(1.-Q.z)*Q.x*(1.-Q.x)-.15*max(Q.y,0.)-1e-4;
    Q.y += .1*Q.x - .1*Q.y-1e-4;
   	Q.z += .1*Q.y - .1*Q.z-1e-4;
    Q = max(Q,0.0);
    vec2 mo = iMouse.xy;
    mo.x = abs(mo.x-0.5*R.x)+0.5*R.x;
    if (iMouse.z>0.&&length(mo-U)<10.) Q.x = 1.;
    if (iFrame < 1) {
    	Q = vec4(exp(-.1*length(U-0.5*R)),0,0,1);
    }
}

