vec4 set_contrast(float contrast,vec2 xy,vec4 texColor)
{
    vec3 finalColor=(texColor.rgb-0.5)*contrast+0.5;
    return vec4(finalColor,1.);
}

vec4 set_brightness(float brightness,vec4 texColor)
{
    texColor.rgb+=brightness;
    return texColor;
}


