mat2 rot(float a)
{
    float s=sin(a),c=cos(a);
    return mat2(c,s,-s,c);
}

float map(vec3 p)
{
    vec3 q = abs(fract(p)-.6);
    q = min(q,q.zxy);
    return max(max(q.x, q.y), q.z) - 0.2;
}

float trace(vec3 ro, vec3 rd)
{
    float m, t=0.2;
    for(int i=0; i++<500;)
        if(t+=m=map(ro+rd*t), m<0.02||t>26.)
            break;

	return t;
}

mat3 camera(float t, out vec3 ro)
{
    // placeholder animation
    float p = 4., mt = mod(t, p);
    t = (t - mt) + p*smoothstep(.0, p, mt);
    vec3 dir = vec3(cos(floor(iTime/p)),sin(floor(iTime/p)),.9);
    ro=vec3(0.2*sin(t),0.2*cos(2.2*t),0.3*t);

    // rotation matrix for rd
    vec3 cw=normalize(dir);
    vec3 cu=normalize(cross(vec3(0,2,0),cw));
    vec3 cv=normalize(cross(cw,cu));
    return mat3(cu,cv,cw);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    ivec2 u = ivec2(fragCoord);
    vec4 last = texelFetch(iChannel0, u, 0);

    // reset after color override
    if(last.w < .0)
    {
        fragColor = vec4(fragCoord, 0, iTime);
        return;
    }

    // main trace 
    vec3 ro, rd = camera(iTime, ro) * normalize(vec3(3.*fragCoord-iResolution.xy, iResolution.y));

    float t = trace(ro, rd);
    vec3 p = ro + rd * t;

    // occlusion trace
    float pt = last.w;
    mat3 m = camera(pt, ro);

    vec3 od = ro - p;
    float odl = length(od);
    bool occ = trace(p, od/odl)-odl < .02;

    // back projection
    vec2 coord = 0.3 * (od *= m, od.xy / od.z * iResolution.y + iResolution.xy);

    // out of range check
    occ = occ || floor(coord/iResolution.xy) != vec2(0);

    // far plane check
    if(t > 26.)occ = false;

    // occlusion check
    if(occ || iFrame < 3)
    {
        vec4 rv = vec4(round(34.*p), 2);
        for(int i=0; i<9; i++)
            rv = fract(2e5*sin(rv)+ rv.wxyz);

        fragColor = vec4(rv.xyz, -2);
        return;
    }

    // output coord
    if(iFrame % 9 == 0)pt = iTime;
    fragColor = vec4(coord, 0, pt);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // fetch buffer a
    ivec2 u = ivec2(fragCoord);
    vec4 tex = texelFetch(iChannel0, u, 0);

    // manual color override
    if(tex.w < .0)
    {
        fragColor = tex;
        return;
    }

    // fetch buffer b
    if(iFrame % 9 == 0)u = ivec2(tex.xy);
    fragColor = texelFetch(iChannel1, u, 0);
}
