float plot(vec2 uv, float value)
{
    if (abs(uv.y - 0.5f) < 0.0012f) {
        return 1.0f; // Check against x-axis
    } else if (abs(uv.y - value) < 0.0012f) {
        return 1.0f; // Check against value of function
    } else {
        return 0.0f; // Otherwise nothing
    }
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord / iResolution.xy;

    // Output to screen

    // Convert pixel x-coordinate to time-varying theta value
    float x_value = (uv.x * 3.141f * 2.0f + iTime * 2.0f);

    vec4 first_wave = vec4(plot(uv, (sin(x_value) + 1.0f) / 2.0f));
    first_wave.x *= 0.3f; // Blue wave

    vec4 second_wave = vec4(plot(uv, (sin(x_value + radians(120.0f)) + 1.0f) / 2.0f));
    second_wave.y *= 0.3f; // Pink (?) wave

    vec4 third_wave = vec4(plot(uv, (sin(x_value + radians(240.0f)) + 1.0f) / 2.0f));
    third_wave.z *= 0.3f; // Yellow (?) wave

	fragColor  = first_wave;
    fragColor += second_wave;
    fragColor += third_wave;
}
