void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec3 col;
    float t = iTime*.5;
    vec2 uv = (.1)*(fragCoord*10.0-iResolution.xy)/iResolution.y;
        int c = 0;
        for(int i=0;i<27;i++)
        {
            c = i % 5;
            float factor = 1.2;
            vec2 uv1 = uv;
            uv /= factor;
            uv += uv1;
            //uv += sin(col.yx);
        	uv += float(i) + (sin(uv.x+uv.y)+cos(uv.y+t)+sin(t-uv.x));
            uv *= factor;
            col[c] = (col[c] + sin(uv.x+uv.y))/2.0;
        }
    col*=2.0;
    fragColor = vec4(col,1.0);

}
