float Square(vec2 uv, float size) {
	vec2 s = abs(uv) - vec2(size);
    float square = length(max(s, .0));
    square = smoothstep(.001, .0, square);
    return square;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
    uv.y -= .025;
    uv.x -= .03;
    uv *= 1.4;
    vec2 gv = uv;
    vec3 col = vec3(0.);

    // animation
    float colorAcc = .15;

    float time = tan(iTime);
    gv.x += time;
    float mod = smoothstep(0.9, 0.0, length(abs(gv.x+(gv.y*-.5))));
    uv.y += mod*mod*.06;
    col -= mod*colorAcc;

    time = tan(iTime+2.);
    gv.x = uv.x;
    gv.x += time;
    mod = smoothstep(0.9, 0.0, length(abs(gv.x+(gv.y*-.5))));
    uv.y += mod*mod*.06;
    col -= mod*colorAcc;

    time = tan(iTime+1.);
    gv.x = uv.x;
    gv.x += time;
    mod = smoothstep(0.9, 0.0, length(abs(gv.x+(gv.y*-.5))));
    uv.y += mod*mod*.06;
    col -= mod*colorAcc;

    // colors
    vec3 yellow = vec3(0.99607, 0.8, 0.0);
    vec3 blue = vec3(0.0, 0.41568, 0.6549);

    // flag
    gv = uv;
    gv.x *= .6;
    float base = Square(gv, .3);
    col += blue * base;

    gv = uv;
    gv.x *= 3.;
    gv.x += .35;
    gv.y *= .669;
    float crossH = Square(gv, .2);
    col -= crossH;
    col += crossH * 2. * yellow;

    gv = uv;
    gv.y *= 8.1;
    float crossV = Square(gv, .5);
    crossV += crossH;
    col -= crossV;
    col += crossV * 2. * yellow;
    col += crossV * 2. * yellow;

    // flagpole
    uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
    gv = uv;
    gv.x += .37;
    gv.y -= .47;
    float pole = smoothstep(.0261, .026, length(gv.x));
    pole -= Square(vec2(gv.x, gv.y), .2);
    col += pole;

    gv.y += 0.19;
    float top = smoothstep(0.0321, 0.032, length(gv));
    col -= top*3.;
    col = clamp(col, 0., 1.);
    col.rg += top;

    col += .05;

    fragColor = vec4(col,1.0);
}
