vec4 hash(vec4 a) {
	return fract(abs(sin(a.ywxz*766.345)+cos(normalize(a)*4972.92855))*2048.97435+abs(a.wxyz)*.2735);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float time = iTime;
    vec2 uv = (fragCoord*2.-iResolution.xy)/iResolution.y;

	float s = 0.;
	for (float p = 1.; p < 1000.; p += 1.+p*.1) {
		vec4 pp = hash(p*vec4(.01,.1,1.,10.));
		s += cos((time*-.5+log(length(uv.xy-(pp.xy*2.-1.))))*p)/p;
	}
	s = pow(clamp(s*.25+.5,0.,1.),2.2);
	fragColor = vec4(mix(vec3(.3,.6,1.)*.3,vec3(1.,.8,.1),s),1);
}
