void mainImage( out vec4 fragColor, in vec2 fragCoord )
{ 
	fragColor = Smp(fragCoord*dif.xy); 
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord*dif.xy;

    float Value = max(Smp(uv).x-DegradingValue,0.) + ((
        Smp(uv).x == 0. &&
        (
        	Smp(uv + dif.xz).x == 1. ||
        	Smp(uv - dif.xz).x == 1. ||
        	Smp(uv + dif.zy).x == 1. ||
        	Smp(uv - dif.zy).x == 1.
     	)) ? 1. : 0.);

    float Mouse = 
        (
        	(length(fragCoord-iMouse.xy) < 20. && iMouse.z > 0.) || 
        	(iTime < 0.1 && length(uv-vec2(0.5, 0.25)) < 0.1) ||
            (iTime < 0.1 && length(uv-vec2(0.25, 0.60)) < 0.1) 
        ) ? 1. : 0.;

    fragColor = vec4(min(Value + Mouse, 1.)); 
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{


    fragColor = Smp(fragCoord*dif.xy); 
}

//General
#define dif vec3(1./iResolution.xy,0.)
#define Smp(uv) texture(iChannel0,uv)

//Flood parameters
//Set this to 0.5 for more interesting effect
#define DegradingValue 0.01
