mat2 rotate(in float a){ float c = cos(a), s = sin(a); return mat2(c, -s, s, c); }
float rasp(vec2 uv, float s, float a, float shift){
  uv *= rotate(a);     
  return  sin(uv.x*s)*9.+shift;
}
float trigrid(vec2 uv, float shift ){
  float r1 = rasp(uv, 50.,0.,shift); 
  float r2 = rasp(uv, 50., 3.14/3.,shift);  
  float r3 = rasp(uv, 50., 3.14/3.*2.,shift); 
  return r1+r2+r3;
}

const vec3 c1 = vec3(.1,.05,.2);
const vec3 c2 = vec3(1,.5,.1);

void mainImage( out vec4 fragColor, in vec2 fragCoord ){
  vec2 uv = (fragCoord-iResolution.xy/2.0) / iResolution.yy *2. ;  

  vec2 uv1 = uv*rotate(-iTime/9.);  
  float r1 = trigrid(uv1, 3.);
  vec2 uv2 = (uv*1.18734+vec2(0,1.25))*rotate(iTime/95.);  
  float r2 = trigrid(uv2, -6.);

  float w = clamp(r1+r2, .0, 1.);
  vec3 col = mix(c1, c2, w);
  col+= vec3(0,0,clamp(r2, 0.,.25));    
  fragColor = vec4(col,1.);
}
